import React from 'react';
import PropTypes from 'prop-types';
import BurgerIngredient from './BurgerIngredient/BurgerIngredient';
import classes from './Burger.module.css';

const burger = props => {

    const ingredients_arr = Object.keys(props.ingredients).map(ingrKey => {
        return [...[props.ingredients[ingrKey]]].map((_, idx) => {
            return <BurgerIngredient key={ingrKey + idx} type={ingrKey} />
        });
    });
    console.log(ingredients_arr);
    return (
      <div className={classes.Burger}>
        {/* {
                props.ingredients.map( (ingr, idx) => <BurgerIngredient key={idx} type={ingr} />)
            } */}
        <BurgerIngredient type="bread-top" />
        {ingredients_arr}
        <BurgerIngredient type="bread-bottom" />
      </div>
    );
}

burger.propTypes = {
    ingredients: PropTypes.object.isRequired,
}

export default burger;
