import React, { Component } from 'react';
import BuildControls from '../../Components/Burger/BuildControls/BuildControls';
import Aux from '../../hoc/Aux';
import Burger from './../../Components/Burger/Burger';
import IngredientContext from '../../Context/ingredientContext/ingredient-context';

class BurgerBuilder extends Component {

    constructor(props) {
        super(props);
        //console.log('[BurgerBuilder.js] -> constructor() - 1');

        this.state = {
            ingredients: {

                salad: 1,
                bacon: 1,
                cheese: 1,
                meat : 1,
            },
        }
    }

    //#region event Handlers

    addIngredientHandler = (type) => {
        const ingredients = {...this.state.ingredients};
        ingredients[type] += 1;
        //#region other soulution
        //const updatedIngredients = {...Ingredients};
        // const oldCount = this.state.ingredients[type];
        // const updatedCount = oldCount + 1;
        // const updatedIngredients = {...this.state.ingredients};
        // updatedIngredients[type] = updatedCount;
        //#endregion
        this.setState({ingredients: ingredients});
    }


    removeIngredientHandler = (type) => {
        const ingredients = {...this.state.ingredients};
        //ingredients[type] > 0 ? ingredients[type] -= 1 : ingredients[type] = 0;
        ingredients[type] -= 1;
        //#region other soulution
        //const updatedIngredients = {...Ingredients};
        // const oldCount = this.state.ingredients[type];
        // const updatedCount = oldCount + 1;
        // const updatedIngredients = {...this.state.ingredients};
        // updatedIngredients[type] = updatedCount;
        //#endregion
        this.setState({ingredients: ingredients});
    }
    //#endregion

    render() {
        //console.log('[BurgerBuilder.js] -> render() - 2');

        return (
          <Aux>
            <Burger ingredients={this.state.ingredients} />
            <IngredientContext.Provider value={this.state.ingredients}>
              <BuildControls
                removeIngredient={this.removeIngredientHandler}
                addIngredient={this.addIngredientHandler}
              />
            </IngredientContext.Provider>
          </Aux>
        );
    }

    componentDidMount() {
        //console.log('[BurgerBuilder.js] -> componentDidMount() - 3');
    }
}

export default BurgerBuilder;
